import { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
// import axios from "axios";
import styles from './ShoppingCart.module.css'
import { setShoppingCart } from '../../redux/actions'
import { Box } from '@mui/system'
import { Typography } from '@mui/material'
import {
  FormControl,
  InputLabel,
  Input,
  Button,
  Select,
  MenuItem,
} from '@mui/material'

const ShoppingCart = () => {
  const shoppingCart = useSelector(state => state.shoppingCart)
  console.log('Carrito de LLEGADA', shoppingCart)
  const initialCartState = shoppingCart.map(product => ({
    ...product,
    quantity: product.quantity || 1,
  }))
  const consolidatedCart = initialCartState.reduce((accumulator, product) => {
    const existingProduct = accumulator.find(item => item.id === product.id) //accumulator Corre los productos del carrito y compara con el ID de cada producto

    if (existingProduct) {
      //existingProduct devuelve el primer objeto que cumplio la condicion
      existingProduct.quantity += product.quantity // Suma 'quantity' si el ID ya existe
    } else {
      // No borrar lo que tenía el objeto
      accumulator.push({ ...product })
    }

    return accumulator
  }, [])

  // Establecer el estado del carrito consolidado
  const [cart, setCart] = useState(consolidatedCart)

  const dispatch = useDispatch()

  const buyProducts = async products => {
    console.log('Simular Que compra los productos')
    alert(
      'Simular Que compra los productos, dado que no se ha hecho la conexion con el BACK'
    )
    //! Cuando haga el backend
    // try {
    //   console.log(products);
    //   const response = await axios.post(
    //     "http://localhost:3001/MercadoPago",//!usar : API_URL
    //     products
    //   );

    //   // Realiza la redirección al enlace de pago
    //   window.location.href = response.data;
    // } catch (error) {
    //   console.error("Error al procesar el pago:", error.message);
    //   // Puedes manejar el error según tus necesidades
    // }
  }

  const setQuantity = (id, newQuantity) => {
    console.log(`id: ${id} nueva cantidad: ${newQuantity}`)
    const updatedCart = cart.map(item =>
      item.id === id ? { ...item, quantity: newQuantity } : item
    )

    console.log('carrito nuevo: ', updatedCart)

    setCart(updatedCart)
  }

  const textStyle = {
    color: 'white',
    marginTop: 3,
  }

  const renderPaymentCard = () => {
    const totalPurchase = cart.reduce(
      (total, product) => total + product.quantity * product.price,
      0
    )
    return (
      <Box sx={{display: 'flex', alignItems: 'center', justifyContent: 'space-evenly'}} >
        <Typography style={textStyle} variant='h3'>
          Total: ${totalPurchase.toFixed(2)}
        </Typography>
        <Button
          variant='contained'
          size='large'
          sx={{
            backgroundColor: '#42e268',
            '&:hover': {
              backgroundColor: '#00ff3d',
            },
          }}
          onClick={() => buyProducts(cart)}
          disabled={cart.length === 0}
        >
          Comprar
        </Button>
      </Box>
    )
  }

  const renderProducts = () => {
    return (
      <Box
        sx={{
          display: 'flex',
          flexWrap: 'wrap',
          justifyContent: cart.length === 1 ? 'center' : 'space-evenly',
        }}
      >
        {cart.map(product => (
          <Box
            sx={{
              display: 'flex',
              backgroundColor: '#414141',
              border: '2px solid #42e268',
              padding: 5,
              borderRadius: 3,
              alignItems: 'center',
            }}
          >
            <Box
              key={product.id}
              sx={{
                display: 'flex',
                flexDirection: 'column',
                minWidth: '500px',
                alignItems: 'center',
                marginTop: 3,
              }}
            >
              <Typography style={textStyle} variant='h5'>
                {product.name}
              </Typography>
              <Typography style={textStyle} variant='h6'>
                Stock: {product.stock}
              </Typography>
              <FormControl
                sx={{ width: '100px', marginTop: 3, marginBottom: 3 }}
              >
                <InputLabel htmlFor='quantity' sx={{ color: '#fff' }}>
                  Unidades:
                </InputLabel>
                <Input
                  id='quantity'
                  name='quantity'
                  type='number'
                  sx={{ color: '#fff' }}
                  value={product.quantity}
                  onChange={e => {
                    let newQuantity = parseInt(e.target.value, 10)
                    if (newQuantity < 1) newQuantity = 1
                    if (newQuantity > product.stock) newQuantity = product.stock
                    setQuantity(product.id, newQuantity)
                  }}
                />
              </FormControl>
              <Typography style={textStyle} variant='h5'>
                Precio: ${product.price}
              </Typography>
              <Button
                variant='contained'
                sx={{
                  backgroundColor: '#ff4646',
                  marginTop: 3,
                  '&:hover': {
                    backgroundColor: '#ff0000',
                  },
                }}
                onClick={() => deleteProduct(product.id)}
              >
                Borrar
              </Button>
            </Box>
            <img
              src={
                'data:image/png;base64,'
              }
              alt={product.name}
              style={{ maxWidth: '100%', height: 'auto', borderRadius: '5px' }}
            />
          </Box>
        ))}
      </Box>
    )
  }

  // const addQuantity = product => {
  //   const existingProduct = cart.find(item => item.id === product.id)
  //   if (existingProduct) {
  //     if (existingProduct.quantity < product.stock) {
  //       const newCart = cart.map(item =>
  //         item.id === product.id
  //           ? { ...item, quantity: item.quantity + 1 }
  //           : item
  //       )
  //       setCart(newCart)
  //     } else {
  //       alert('No hay suficiente stock disponible')
  //     }
  //   } else {
  //     const productWithQuantity = { ...product, quantity: 1 }
  //     setCart([...cart, productWithQuantity])
  //   }
  // }

  // const reduceQuantity = id => {
  //   const newCart = cart.map(item =>
  //     item.id === id && item.quantity > 1
  //       ? { ...item, quantity: item.quantity - 1 }
  //       : item
  //   )

  //   const updatedCart = newCart.filter(item => item.quantity > 0)
  //   setCart(updatedCart)
  // }

  const deleteProduct = id => {
    const newCart = cart.filter(item => item.id !== id)
    setCart(newCart)
  }

  useEffect(() => {
    dispatch(setShoppingCart(cart))
    console.log('Carrito actualizado:', cart)
  }, [dispatch, cart])

  return (
    <div>
      <h1>Shopping Cart</h1>

      <div className={styles['contenedor-productos']}>{renderProducts()}</div>
      <div className={styles['pago-container']}>{renderPaymentCard()}</div>
    </div>
  )
}

export default ShoppingCart
